"use client";

import React, { useRef, useState } from "react";
import { IoReload } from "react-icons/io5";
import SignatureCanvas from "react-signature-canvas";

interface SignatureData {
  id: string;
  title: string;
  details: string;
  dataUrl: string; // signature as image (base64)
}

export default function SignatureForm() {
  const [signatures, setSignatures] = useState<SignatureData[]>([
    {
      dataUrl:
        "data:image/png;base64,",
      details: "john doe signature",
      id: "1758203088821",
      title: "John Doe",
    },
    {
      dataUrl:
        "data:image/png;base64,",
      details: "siweh sarris signature",
      id: "1758203132117",
      title: "Siweh Harris",
    },
  ]);

  const [selectedId, setSelectedId] = useState<string | null>("1758203088821");

  const sigCanvas = useRef<SignatureCanvas>(null);
  const [title, setTitle] = useState("");
  const [details, setDetails] = useState("");

  // Add new empty signature
  const handleAddSignature = () => {
    const newSig: SignatureData = {
      id: Date.now().toString(),
      title: "Untitled",
      details: "",
      dataUrl: "",
    };
    setSignatures((prev) => [...prev, newSig]);
    setSelectedId(newSig.id);
    setTitle("Untitled");
    setDetails("");
    sigCanvas.current?.clear();
  };

  // Save signature
  const handleSave = () => {
    if (!selectedId || !sigCanvas.current) return;
    const updated = signatures.map((sig) =>
      sig.id === selectedId
        ? {
            ...sig,
            title,
            details,
            dataUrl: sigCanvas
              .current!.getTrimmedCanvas()
              .toDataURL("image/png"),
          }
        : sig
    );
    setSignatures(updated);
  };

  console.log(signatures);

  // Delete signature
  const handleDelete = () => {
    if (!selectedId) return;
    setSignatures(signatures.filter((sig) => sig.id !== selectedId));
    setSelectedId(null);
    setTitle("");
    setDetails("");
    sigCanvas.current?.clear();
  };

  // When selecting from list
  const handleSelect = (sig: SignatureData) => {
    sigCanvas.current?.clear();
    setSelectedId(sig.id);
    setTitle(sig.title);
    setDetails(sig.details);
    sigCanvas.current?.fromDataURL(sig.dataUrl || "");
  };

  return (
    <div className="flex bg-gray-50 gap-10">
      {/* Sidebar */}
      <div className="w-64 border border-[#E3E3E3] rounded-lg">
        <button
          onClick={handleAddSignature}
          className="w-full cursor-pointer border-l-4 font-semibold border-[var(--primary)] p-4 text-[var(--primary)]"
        >
          <span className="text-2xl">+</span> &nbsp;&nbsp; Add Signature
        </button>

        <div>
          {signatures.map((sig) => (
            <div
              key={sig.id}
              onClick={() => handleSelect(sig)}
              className={`cursor-pointer border border-[#E3E3E3] border-l-4 border-l-[var(--primary)] p-2 ${
                sig.id === selectedId
                  ? "bg-[var(--primary)] text-white"
                  : "bg-transparent text-black"
              }`}
            >
              <p className="font-medium">{sig.title}</p>
              <p
                className={`text-xs ${
                  sig.id === selectedId ? "text-gray-200" : "text-gray-600"
                } truncate`}
              >
                {sig.details || "No details"}
              </p>
            </div>
          ))}
        </div>
      </div>

      {/* Main Content */}
      <div className="flex-1 p-6 bg-white rounded-lg border border-[#E3E3E3]">
        {selectedId ? (
          <div>
            <div className="flex justify-between items-center mb-2">
              <h2 className="text-xl font-bold">Signature</h2>
              <button
                onClick={handleDelete}
                className="text-red-500 hover:underline"
              >
                Delete
              </button>
            </div>

            {/* Signature Canvas */}
            <div className="rounded-lg border border-[#E3E3E3] bg-white p-2 mb-4">
              <SignatureCanvas
                ref={sigCanvas}
                penColor="black"
                canvasProps={{ width: 600, height: 200, className: "bg-white" }}
              />
              <button
                onClick={() => sigCanvas.current?.clear()}
                className="flex items-center gap-2 py-2 mt-2 rounded-lg border border-red-500 bg-red-100 px-3 text-red-600 hover:bg-red-200 transition-all cursor-pointer"
              >
                <IoReload size={20} className="text-red-600" />
                Clear
              </button>
            </div>

            {/* Title + Details */}
            <div className="grid grid-cols-2 gap-4 mb-4">
              <div>
                <label htmlFor="title" className="font-semibold">
                  Signature Title
                </label>
                <input
                  type="text"
                  id="title"
                  placeholder="Signature Title"
                  value={title}
                  onChange={(e) => setTitle(e.target.value)}
                  className="w-full mt-2 rounded-lg border border-[#E3E3E3] px-2 py-3"
                />
              </div>
              <div>
                <label htmlFor="title" className="font-semibold">
                  Details
                </label>
                <input
                  type="text"
                  placeholder="Details"
                  value={details}
                  onChange={(e) => setDetails(e.target.value)}
                  className="w-full mt-2 rounded-lg border border-[#E3E3E3] px-2 py-3"
                />
              </div>
            </div>

            <div className="w-full flex justify-end">
              <button
                onClick={handleSave}
                className="rounded bg-[var(--primary)] px-12 cursor-pointer py-2 text-white hover:bg-green-600"
              >
                Save
              </button>
            </div>
          </div>
        ) : (
          <p className="text-gray-600">
            Select a signature or create a new one.
          </p>
        )}
      </div>
    </div>
  );
}
